     *
     * @param   _to is address of user to which tokens will be minted
     * @param   _amount is amount of tokens to be minted
     */
    function mint(address _to, uint256 _amount) public lssMint(_to, _amount) whenNotPaused onlyClassifiedAuthority {
        _mint(_to, _amount);
        emit Mint(_to, _amount);
    }

    /**
